   	AND group_type = #{groupType,jdbcType=TINYINT}
    
    
   	AND name like #{name,jdbcType=VARCHAR}
    
	
      
    order by update_time desc
    
      
    order by  ${property} ${direction}
    
    limit  #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

    SELECT
	count(1)
    FROM
    t_user_group_v2 
	
	
   	AND group_type = #{groupType,jdbcType=TINYINT}
    
    
   	AND name like #{name,jdbcType=VARCHAR}

