SELECT ug.id as id ,u.realname,u.id as userId,u.nickname nickName,u.mobile,cy.city_name cityName,u.order_count orderCount,u.last_login_time loginTime,o.create_time ordersTime,u.status userStatus,o.plate_no plateNO,ug.create_time as joinTime FROM t_user u LEFT JOIN ( SELECT c.* FROM o_orders c, ( SELECT a.user_id uid, max(a.create_time) maxtime FROM o_orders a GROUP BY uid ) b WHERE c.user_id = b.uid AND c.create_time = b.maxtime ) o ON u.id = o.user_id LEFT JOIN v_prefecture p ON o.pre_id = p.id LEFT JOIN t_city cy ON p.city_id = cy.id INNER JOIN t_user_group_detail ug on ug.user_id=u.id where u.user_type = 1
and ug.user_group_id = #{userGroupId}
and u.order_count = #{ordersCount}
and u.app_register_time is not null and u.wechat_bind_time is not null
and u.status = 4
and nickname like #{nickname,jdbcType=VARCHAR}
and mobile = #{mobile,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
and o.plate_no= #{plateNo,jdbcType=VARCHAR}
order by u.create_time desc
order by ${property} ${direction}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM t_user u LEFT JOIN ( SELECT c.* FROM o_orders c, ( SELECT a.user_id uid, max(a.create_time) maxtime FROM o_orders a GROUP BY uid ) b WHERE c.user_id = b.uid AND c.create_time = b.maxtime ) o ON u.id = o.user_id LEFT JOIN v_prefecture p ON o.pre_id = p.id LEFT JOIN t_city cy ON p.city_id = cy.id INNER JOIN t_user_group_detail ug on ug.user_id=u.id where u.user_type = 1
and ug.user_group_id = #{userGroupId}
and u.order_count = #{ordersCount}
and u.app_register_time is not null and u.wechat_bind_time is not null
and u.status = 4
and nickname like #{nickname,jdbcType=VARCHAR}
and mobile = #{mobile,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
and o.plate_no= #{plateNo,jdbcType=VARCHAR}

SELECT u.realname,u.id as id,u.nickname nickName,u.mobile,cy.city_name cityName,u.order_count orderCount,u.last_login_time loginTime,o.create_time ordersTime,u.status userStatus,o.plate_no plateNO FROM t_user u LEFT JOIN ( SELECT c.* FROM o_orders c, ( SELECT a.user_id uid, max(a.create_time) maxtime FROM o_orders a GROUP BY uid ) b WHERE c.user_id = b.uid AND c.create_time = b.maxtime ) o ON u.id = o.user_id LEFT JOIN v_prefecture p ON o.pre_id = p.id LEFT JOIN t_city cy ON p.city_id = cy.id where u.user_type = 1
and u.id not in (select user_id from t_user_group_detail where user_group_id = #{notInGroupId,jdbcType=BIGINT} )
and u.order_count = #{ordersCount}
and u.app_register_time is not null and u.wechat_bind_time is not null
and u.status = 4
and nickname like #{nickname,jdbcType=VARCHAR}
and mobile = #{mobile,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
and o.plate_no= #{plateNo,jdbcType=VARCHAR}
order by u.create_time desc
order by ${property} ${direction}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(*) FROM t_user u LEFT JOIN ( SELECT c.* FROM o_orders c, ( SELECT a.user_id uid, max(a.create_time) maxtime FROM o_orders a GROUP BY uid ) b WHERE c.user_id = b.uid AND c.create_time = b.maxtime ) o ON u.id = o.user_id LEFT JOIN v_prefecture p ON o.pre_id = p.id LEFT JOIN t_city cy ON p.city_id = cy.id where u.user_type = 1
and u.id not in (select user_id from t_user_group_detail where user_group_id = #{notInGroupId,jdbcType=BIGINT} )
and u.order_count = #{ordersCount}
and u.app_register_time is not null and u.wechat_bind_time is not null
and u.status = 4
and nickname like #{nickname,jdbcType=VARCHAR}
and mobile = #{mobile,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
and o.plate_no= #{plateNo,jdbcType=VARCHAR}

SELECT gd.id, gd.user_group_id, gd.user_id, gd.create_user_id, gd.create_time FROM t_user_group_detail gd inner join t_user_group_v2 g on g.id=gd.user_group_id and g.group_type=1
AND user_group_id = #{userGroupId,jdbcType=BIGINT}
AND user_id = #{userId,jdbcType=BIGINT}
AND user_id in (select veh_user_id from v_vehicle_mark_manage where veh_mark= #{plate,jdbcType=VARCHAR} )

select 
user_group_id,
user_id
from (
select gi.user_group_id,vm.veh_user_id as user_id from t_user_group_input gi
inner join t_user_group_v2 g on g.id=gi.user_group_id and g.group_type=1
inner join v_vehicle_mark_manage vm on vm.veh_mark=gi.plate 

	AND gi.user_group_id = #{userGroupId,jdbcType=BIGINT}


	AND vm.veh_user_id = #{userId,jdbcType=BIGINT}


	AND vm.veh_mark = #{plate,jdbcType=VARCHAR}

union
select gi.user_group_id,tu.id as user_id from t_user_group_input gi
inner join t_user_group_v2 g on g.id=gi.user_group_id and g.group_type=1
inner join t_user tu on tu.username=gi.mobile

	AND gi.user_group_id = #{userGroupId,jdbcType=BIGINT}


	AND gi.user_id = #{userId,jdbcType=BIGINT}


	gi.plate = #{plate,jdbcType=VARCHAR}


) as t
