mapper.select:
select id, veh_user_id, veh_mark, create_time, update_time
from v_vehicle_mark_manage
where id = #{id,jdbcType=BIGINT}

select count(id) 
from v_vehicle_mark_manage 
where veh_mark = #{vehMark} and veh_user_id = #{userId}

select count(id) 
from v_vehicle_mark_manage 
where veh_user_id = #{userId}

select count(id) 
from v_vehicle_mark_manage 
where id = #{id}

select id, veh_user_id, veh_mark, create_time, update_time 
from v_vehicle_mark_manage where veh_user_id = #{userId}
ORDER BY update_time DESC
