mapper.select:
select 

from t_ent_rented_record
where user_id = #{userId,jdbcType=BIGINT}
and stall_id = #{stallId,jdbcType=BIGINT}
and status = 0 order by id desc limit 0,1

select count(1) from t_ent_rented_record where 1=1
AND stall_id = #{stallId}
AND user_id <> #{userId}
and status = 0

		select
		  record.id, record.ent_id, record.ent_pre_id, record.plate_no, record.down_time, record.leave_time, record.stall_id, record.stall_name, record.pre_id, 
          record.pre_name,en.name ent_name
		from t_ent_rented_record record left join t_biz_enterprise en on record.ent_id = en.id
		where 1 = 1
		
			AND pre_name like #{preName}
		
		
			AND en.name like #{entName}
		
		
	    and down_time >= #{downTime}
	     
	    
	    and leave_time <= #{leaveTime}
	     
		
			order by down_time desc
		
		
			order by ${property} ${direction}
		
		limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

	select
	count(1)
	from t_ent_rented_record record left join t_biz_enterprise en on record.ent_id = en.id
	where 1 = 1
	
		AND pre_name like #{preName}
	
	
		AND en.name like #{entName}
	
	
    and down_time >= #{downTime}
     
    
    and leave_time <= #{leaveTime}

		select t.plate_no,t.down_time,t.leave_time,t.stall_name, t.pre_name,u.username as mobile,t.`status` from t_ent_rented_record t left join t_user u on t.user_id= u.id
 		where 1 = 1
		
			AND pre_name like #{preName}
		
		
	    and t.down_time >= #{downTime}
	     
	    
	    and t.leave_time <= #{leaveTime}
	     
	    order by down_time desc
