mapper.select:
SELECT * FROM (SELECT a.fixed_id,b.start_time, b.end_time,d.username,d.id as userid,h.leave_time,b.pre_id, GROUP_CONCAT(c.veh_mark) as plates,z.stalls, (CASE when e.id is NULL THEN 1 ELSE 0 end) pick FROM t_fixed_plate a LEFT JOIN t_fixed_rent b ON a.fixed_id = b.id LEFT JOIN v_vehicle_mark_manage c ON a.plate_no = c.veh_mark LEFT JOIN t_user d ON c.veh_user_id = d.id LEFT JOIN t_fixed_disable e ON a.fixed_id = e.fixed_id AND d.id = e.user_id LEFT JOIN (SELECT fixed_id,GROUP_CONCAT(g.stall_name) as stalls FROM t_fixed_stall f LEFT JOIN v_stall g ON f.stall_id = g.id WHERE f.status=1 GROUP BY fixed_id ) z ON a.fixed_id = z.fixed_id LEFT JOIN (SELECT leave_time,user_id FROM (select * from t_ent_rented_record order by leave_time desc limit 100000 ) t GROUP BY user_id) h ON h.user_id =d.id WHERE d.username != " " AND c.veh_mark != "" AND c.veh_mark in (SELECT plate_no FROM t_fixed_plate) GROUP BY a.fixed_id,c.veh_user_id) al where 1=1
and username = #{mobile}
and pre_id = #{createUserId}
and plates like CONCAT('%',#{plates},'%')
order by ${property} ${direction}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM (SELECT a.fixed_id,b.start_time, b.end_time,d.username,d.id as userid,h.leave_time,b.pre_id, GROUP_CONCAT(c.veh_mark) as plates,z.stalls, (CASE when e.id is NULL THEN 1 ELSE 0 end) pick FROM t_fixed_plate a LEFT JOIN t_fixed_rent b ON a.fixed_id = b.id LEFT JOIN v_vehicle_mark_manage c ON a.plate_no = c.veh_mark LEFT JOIN t_user d ON c.veh_user_id = d.id LEFT JOIN t_fixed_disable e ON a.fixed_id = e.fixed_id AND d.id = e.user_id LEFT JOIN (SELECT fixed_id,GROUP_CONCAT(g.stall_name) as stalls FROM t_fixed_stall f LEFT JOIN v_stall g ON f.stall_id = g.id WHERE f.status=1 GROUP BY fixed_id ) z ON a.fixed_id = z.fixed_id LEFT JOIN (SELECT leave_time,user_id FROM (select * from t_ent_rented_record order by leave_time desc limit 100000 ) t GROUP BY user_id) h ON h.user_id =d.id WHERE d.username != " " AND c.veh_mark != "" AND c.veh_mark in (SELECT plate_no FROM t_fixed_plate) GROUP BY a.fixed_id,c.veh_user_id) al where 1=1
and username = #{mobile}
and pre_id = #{createUserId}
and plates like CONCAT('%',#{plates},'%')
