mapper.select:
    SELECT
    eu.*, e.end_time,e.company_name
    FROM
    t_ent_rent_ent_user eu left join t_ent_rent_ent e on eu.rent_ent_id = e.id
	
    
   	AND eu.rent_ent_id = #{rentEntId}
    
    
   	AND eu.username like #{username}
    
    
   	AND eu.plate = #{plate}
    
    
   	AND eu.mobile like #{mobile}
    
	
      
    order by e.start_time desc
    
      
    order by  ${property} ${direction}
    
    limit  #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

    SELECT
	count(1)
    FROM
    t_ent_rent_ent_user 
	
    
   	AND rent_ent_id like #{rentEntId}
    
    
   	AND username like #{username}
    
    
   	AND plate = #{plate}
    
    
   	AND mobile like #{mobile}

select 

from t_ent_rent_ent_user
where 1 = 1

	and rent_ent_id = #{rentEntId,jdbcType=INTEGER}


	and plate = #{plate,jdbcType=VARCHAR}

limit 1
