mapper.select:
   	select count(1) from t_acc_wallet_detail
		where 1 = 1
    
		AND u.username = #{username}
	
    
		AND d.type = #{type}
	
    
		AND d.source = #{source}
	
    
    and DATE_FORMAT(d.create_time,'%Y-%m-%d') >= #{startTime}
     
    
    and DATE_FORMAT(d.create_time,'%Y-%m-%d') <= #{endTime}

  	select d.id,d.account_id,d.amount,d.source,d.type,d.create_time,d.account_amount,u.username as username,u.sex as sex
  	from t_acc_wallet_detail d left join t_user u on d.account_id = u.id 
		where 1 = 1
    
		AND u.username = #{username}
	
    
		AND d.type = #{type}
	
    
		AND d.source = #{source}
	
    
    and DATE_FORMAT(d.create_time,'%Y-%m-%d') >= #{startTime}
     
    
    and DATE_FORMAT(d.create_time,'%Y-%m-%d') <= #{endTime}
     
        
    order by d.create_time desc
    
      
    order by  ${property} ${direction}
    
     limit  #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

  	select d.id,d.account_id,d.amount,d.source,d.type,d.create_time,d.account_amount,u.username as username,u.sex as sex
  	from t_acc_wallet_detail d left join t_user u on d.account_id = u.id 
		where 1 = 1
    
		AND u.username = #{mobile}
	
    
		AND d.type = #{type}
	
    
		AND d.source = #{source}
	
    
    and DATE_FORMAT(d.create_time,'%Y-%m-%d') >= #{startTime}
     
    
    and DATE_FORMAT(d.create_time,'%Y-%m-%d') <= #{endTime}
     
    order by d.create_time desc
