mapper.select:
  	select id, stall_name, pre_id,lock_sn, status, type
	from v_stall 
	where id in  
    
       #{id}
    
    
       and type = #{type,jdbcType=SMALLINT}

  	select id, stall_name, lock_sn, status, type, pre_id
	from v_stall 
	where 1=1 
	  
    and stall_name like #{name}
     
      
    and status = #{status}
     
      
    and type = #{type}
     
      
    and pre_id = #{preId}
    
      
    and brand = #{brand}
    
      
    order by lock_voltage desc
    
      
    order by  ${property} ${direction}

    SELECT count(1) FROM v_stall l
	WHERE l.pre_id = #{preId}
    and l.stall_name = #{stallName}
      
    and l.id != #{id}

    SELECT count(1) FROM v_stall l
	WHERE l.pre_id = #{preId}
      
    and l.status = #{status}
    
      
    and l.stall_name like #{stallName}
    
      
    and l.lock_sn like #{lockSn}

    SELECT
	*
	FROM
		v_stall l
	WHERE
	l.pre_id = #{preId}
      
    and l.status = #{status}
    
      
    and l.stall_name like #{stallName}
    
      
    and l.lock_sn like #{lockSn}
    
      
     order by l.create_time desc
    
      
    order by  l.${property} ${direction}
    
    limit  #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

    SELECT
	*
	FROM
		v_stall l
	WHERE 1=1
	
		and l.pre_id = #{preId}
	
      
    and l.status = #{status}
    
      
    and l.stall_name like #{stallName}
    
      
    and l.lock_sn like #{lockSn}
    
      
     order by l.create_time desc
    
      
    order by  l.${property} ${direction}
    
    limit  #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
