mapper.select:
select count(1) 
from t_stall_operate_log
where 1 = 1
  
and source = #{source}
 
  
and operation = #{operation} 
 
  
and status = #{status} 
 

and create_time >= #{startTime} 
 

and create_time <= #{endTime}

	select o.id, o.operator_id, o.source, o.stall_id, o.operation, o.remark, o.create_time, 
	o.status,s.stall_name,u.realname as operator
	from t_stall_operate_log o 
	left join t_admin_user u on o.operator_id = u.id 
	left join v_stall s on o.stall_id = s.id 
	where 1=1 
      
    and o.id = #{id}
     
      
    and o.source = #{source}
     
      
    and o.operation = #{operation} 
     
      
    and o.status = #{status} 
     
    
    and o.create_time >= #{startTime} 
     
    
    and o.create_time <= #{endTime}
    
      
    order by o.create_time desc
    
      
    order by  ${property} ${direction}
    
    limit  #{start},#{pageSize}

	select o.id, o.operator_id, o.source, o.stall_id, o.operation, o.remark, o.create_time, 
	o.status,s.stall_name,u.realname as operator
	from t_stall_operate_log o 
	left join t_admin_user u on o.operator_id = u.id 
	left join v_stall s on o.stall_id = s.id 
	where 1=1 
      
    and o.source = #{source}
     
      
    and o.operation = #{operation} 
     
      
    and o.status = #{status} 
     
    
    and o.create_time >= #{startTime} 
     
    
    and o.create_time <= #{endTime}
    
    order by o.create_time desc
