mapper.select:
select t.id,t.name from v_prefecture t where 1 = 1 
  
	and t.city_id = #{cityId,jdbcType=BIGINT}
  
and id in (select pre_id from t_coupon_template t where t.type = 2)

SELECT count(*) as sum_total, t1.DAY, t1.day_total , t1.stall_total FROM t_user t, ( SELECT DATE_FORMAT(t.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total, (SELECT count(*) FROM v_stall) AS stall_total FROM t_user t WHERE ( t.is_app_register = 1 OR t.is_wechat_bind = 1 )
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
GROUP BY DAY ORDER BY DAY DESC ) t1 WHERE ( t.is_app_register = 1 OR t.is_wechat_bind = 1 ) AND t.create_time < t1.day group by day order by day desc

SELECT t3. DAY, IFNULL(t4.bjxclx, 0) AS bjxclx, IFNULL(t4.hzxclx, 0) AS hzxclx, t3.zrzh FROM ( SELECT t1. DAY, t1.day_total, t1.day_total - IFNULL(t2.day_total, 0) AS zrzh FROM ( SELECT DATE_FORMAT(t.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM t_user t WHERE ( t.is_app_register = 1 OR t.is_wechat_bind = 1 )
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
GROUP BY DAY ) t1 LEFT JOIN ( SELECT DATE_FORMAT(t.create_time, '%Y-%m-%d') AS DAY, IFNULL(count(*), 0) AS day_total FROM t_coupon_send_user t LEFT JOIN t_coupon_template temp ON t.template_id = temp.id LEFT JOIN t_user u ON u.id = t.user_id WHERE temp.type = 2 AND temp. STATUS = 1
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
GROUP BY DAY ) t2 ON t1. DAY = t2. DAY ) t3 LEFT JOIN ( SELECT DAY, MAX( CASE city_name WHEN '北京' THEN day_total ELSE 0 END ) bjxclx, MAX( CASE city_name WHEN '杭州' THEN day_total ELSE 0 END ) hzxclx FROM ( SELECT c.id, c.city_name, DATE_FORMAT(t.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM t_coupon_send_user t LEFT JOIN t_coupon_template temp ON t.template_id = temp.id LEFT JOIN v_prefecture pre ON temp.pre_id = pre.id LEFT JOIN t_city c ON pre.city_id = c.id WHERE temp.type = 2 AND temp. STATUS = 1 AND pre. STATUS = 0
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
GROUP BY c.id, c.city_name, DAY ) t GROUP BY DAY ) t4 ON t3. DAY = t4. DAY ORDER BY DAY DESC

SELECT c.id, c.city_name, pre.id AS pre_id, pre. NAME AS pre_name, DATE_FORMAT(t.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM t_coupon_send_user t LEFT JOIN t_coupon_template temp ON t.template_id = temp.id LEFT JOIN v_prefecture pre ON temp.pre_id = pre.id LEFT JOIN t_city c ON pre.city_id = c.id WHERE temp.type = 2 AND temp. STATUS = 1 AND pre. STATUS = 0 and pre.id in
#{id}
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
GROUP BY c.id, c.city_name, pre.id, pre. NAME, DAY

SELECT c.id, c.city_name, pre.id AS pre_id, pre. NAME AS pre_name, DATE_FORMAT(t.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total, pre.stall_total, ROUND(count(*) / pre.stall_total, 1) AS average FROM t_coupon_send_user t LEFT JOIN t_coupon_template temp ON t.template_id = temp.id LEFT JOIN ( SELECT p.id, p. NAME, p.city_id, count(*) AS stall_total FROM v_stall s LEFT JOIN v_prefecture p ON p.id = s.pre_id WHERE p. STATUS = 0 GROUP BY p.id, p. NAME, p.city_id ) pre ON temp.pre_id = pre.id left join t_city c on c.id = pre.city_id WHERE temp.type = 2 AND temp. STATUS = 1 and pre.id in
#{id}
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
GROUP BY c.id, c.city_name, pre.id, pre.NAME, pre.stall_total, DAY

SELECT c.id, c.city_name, pre.id AS pre_id, pre.NAME AS pre_name, DATE_FORMAT(o.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM o_orders o LEFT JOIN v_prefecture pre ON o.pre_id = pre.id LEFT JOIN t_city c on pre.city_id = c.id WHERE pre. STATUS = 0 AND o. STATUS = 3 AND pre.id in
#{id}
AND TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) >= 15
AND o.create_time > #{startTime}
AND o.create_time < #{endTime}
GROUP BY c.id, c.city_name, pre.id, pre.NAME, DAY

SELECT c.id, c.city_name, pre.id AS pre_id, pre.NAME AS pre_name, DATE_FORMAT(o.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM o_orders o LEFT JOIN v_prefecture pre ON o.pre_id = pre.id LEFT JOIN t_city c on pre.city_id = c.id WHERE pre. STATUS = 0 AND o. STATUS = 3 AND o.order_no LIKE '%YL%' AND pre.id in
#{id}
AND TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) >= 15
AND o.create_time > #{startTime}
AND o.create_time < #{endTime}
GROUP BY c.id, c.city_name, pre.id, pre.NAME, DAY

SELECT c.id, c.city_name, pre.id pre_id, pre.NAME pre_name, DATE_FORMAT(o.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM o_orders o LEFT JOIN v_prefecture pre ON o.pre_id = pre.id LEFT JOIN t_user u ON o.user_id = u.id LEFT JOIN t_city c on pre.city_id = c.id WHERE pre. STATUS = 0 AND o. STATUS = 3 AND pre.id in
#{id}
AND TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) >= 15
AND o.create_time > #{startTime}
AND o.create_time < #{endTime}
AND DATE_FORMAT(u.create_time, '%Y-%m-%d') = DATE_FORMAT(o.create_time, '%Y-%m-%d') GROUP BY c.id, c.city_name, pre.id, pre.NAME, DAY

SELECT c.id, c.city_name, pre.id pre_id, pre.NAME pre_name, DATE_FORMAT(o.create_time, '%Y-%m-%d') AS DAY, count(*) AS day_total FROM o_orders o LEFT JOIN v_prefecture pre ON o.pre_id = pre.id LEFT JOIN t_user u ON o.user_id = u.id LEFT JOIN t_city c on pre.city_id = c.id WHERE pre. STATUS = 0 AND o. STATUS = 3 AND pre.id in
#{id}
AND TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) >= 15
AND o.create_time > #{startTime}
AND o.create_time < #{endTime}
AND DATE_FORMAT(u.create_time, '%Y-%m-%d') != DATE_FORMAT(o.create_time, '%Y-%m-%d') GROUP BY c.id, c.city_name, pre.id, pre.NAME, DAY

SELECT c.id, c.city_name, pre.id pre_id, pre.NAME pre_name, DATE_FORMAT(o.create_time, '%Y-%m-%d') AS DAY, sum( TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) ) AS total_time, pre.stall_total, count(*) AS day_total, ROUND(count(*) / stall_total, 1) AS rdl, ROUND( sum( TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) ) / 60 / count(*), 1 ) AS jtsc, ROUND( sum( TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) ) / 60 / stall_total, 1 ) AS runtime FROM o_orders o LEFT JOIN ( SELECT p.id, p.NAME, p.city_id, p.status, count(*) AS stall_total FROM v_stall s LEFT JOIN v_prefecture p ON p.id = s.pre_id GROUP BY p.id, p.NAME, p.city_id, p.status ) pre ON o.pre_id = pre.id LEFT JOIN t_city c on c.id = pre.city_id WHERE o.STATUS = 3 AND pre.STATUS = 0 AND pre.id in
#{id}
AND TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) >= 15
AND o.create_time > #{startTime}
AND o.create_time < #{endTime}
GROUP BY c.id, c.city_name, pre.id, pre.name, pre.stall_total, DAY

SELECT c.id, c.city_name, pre.id pre_id, pre. NAME pre_name, DATE_FORMAT(o.create_time, '%Y-%m-%d') AS DAY, round( sum(o.total_amount) / count(*), 1 ) AS average_price, sum(o.total_amount) as amount_total, count(*) as day_total FROM o_orders o LEFT JOIN v_prefecture pre ON o.pre_id = pre.id LEFT JOIN t_city c ON pre.city_id = c.id WHERE o. STATUS = 3 AND pre. STATUS = 0 AND pre.id in
#{id}
AND TIMESTAMPDIFF( MINUTE, o.create_time, o.end_time ) >= 15
AND o.create_time > #{startTime}
AND o.create_time < #{endTime}
GROUP BY c.id, c.city_name, pre.id, pre. NAME, DAY
